/**
 * Tipos TypeScript para as entidades do sistema
 * Define as interfaces e enums que representam os dados do sistema de controle de gastos
 */

/**
 * Enumeração para os tipos de transação
 * - Despesa: saída de dinheiro
 * - Receita: entrada de dinheiro
 */
export enum TipoTransacao {
  Despesa = 'Despesa',
  Receita = 'Receita'
}

/**
 * Enumeração para as finalidades de uma categoria
 * - Despesa: categoria usada apenas para despesas
 * - Receita: categoria usada apenas para receitas
 * - Ambas: categoria que pode ser usada tanto para despesas quanto receitas
 */
export enum Finalidade {
  Despesa = 'Despesa',
  Receita = 'Receita',
  Ambas = 'Ambas'
}

/**
 * Interface que representa uma pessoa no sistema
 * Contém dados básicos de identificação
 */
export interface Pessoa {
  /** Identificador único gerado automaticamente pelo sistema */
  id: number;
  /** Nome completo da pessoa */
  nome: string;
  /** Idade da pessoa (número inteiro positivo) */
  idade: number;
}

/**
 * Interface para criação de uma nova pessoa
 * Não inclui o ID pois este é gerado automaticamente pelo backend
 */
export interface CriarPessoaDto {
  /** Nome completo da pessoa */
  nome: string;
  /** Idade da pessoa (número inteiro positivo) */
  idade: number;
}

/**
 * Interface que representa uma categoria de transação
 * Categorias são usadas para classificar receitas e despesas
 */
export interface Categoria {
  /** Identificador único gerado automaticamente pelo sistema */
  id: number;
  /** Descrição/nome da categoria (ex: "Alimentação", "Salário") */
  descricao: string;
  /** Define se a categoria é para despesa, receita ou ambos */
  finalidade: Finalidade;
}

/**
 * Interface para criação de uma nova categoria
 * Não inclui o ID pois este é gerado automaticamente pelo backend
 */
export interface CriarCategoriaDto {
  /** Descrição/nome da categoria */
  descricao: string;
  /** Define se a categoria é para despesa, receita ou ambos */
  finalidade: Finalidade;
}

/**
 * Interface que representa uma transação financeira
 * Pode ser uma despesa ou receita associada a uma pessoa e categoria
 */
export interface Transacao {
  /** Identificador único gerado automaticamente pelo sistema */
  id: number;
  /** Descrição detalhada da transação */
  descricao: string;
  /** Valor da transação (número decimal positivo) */
  valor: number;
  /** Tipo da transação (despesa ou receita) */
  tipo: TipoTransacao;
  /** ID da categoria associada */
  categoriaId: number;
  /** ID da pessoa associada */
  pessoaId: number;
  /** Objeto da categoria (carregado do backend) */
  categoria?: Categoria;
  /** Objeto da pessoa (carregado do backend) */
  pessoa?: Pessoa;
}

/**
 * Interface para criação de uma nova transação
 * Não inclui o ID pois este é gerado automaticamente pelo backend
 */
export interface CriarTransacaoDto {
  /** Descrição detalhada da transação */
  descricao: string;
  /** Valor da transação (número decimal positivo) */
  valor: number;
  /** Tipo da transação (despesa ou receita) */
  tipo: TipoTransacao;
  /** ID da categoria associada */
  categoriaId: number;
  /** ID da pessoa associada */
  pessoaId: number;
}

/**
 * Interface para os totais de uma pessoa
 * Utilizada na consulta de totais por pessoa
 */
export interface TotalPorPessoa {
  /** Dados da pessoa */
  pessoa: Pessoa;
  /** Soma total das receitas da pessoa */
  totalReceitas: number;
  /** Soma total das despesas da pessoa */
  totalDespesas: number;
  /** Saldo líquido (receitas - despesas) */
  saldo: number;
}

/**
 * Interface para os totais gerais de todas as pessoas
 * Exibido ao final da listagem de totais por pessoa
 */
export interface TotalGeral {
  /** Soma total de todas as receitas */
  totalReceitas: number;
  /** Soma total de todas as despesas */
  totalDespesas: number;
  /** Saldo líquido geral (total receitas - total despesas) */
  saldoLiquido: number;
}

/**
 * Interface para os totais de uma categoria
 * Utilizada na consulta de totais por categoria (funcionalidade opcional)
 */
export interface TotalPorCategoria {
  /** Dados da categoria */
  categoria: Categoria;
  /** Soma total das receitas nesta categoria */
  totalReceitas: number;
  /** Soma total das despesas nesta categoria */
  totalDespesas: number;
  /** Saldo líquido (receitas - despesas) */
  saldo: number;
}

/**
 * Interface para resposta da consulta de totais por pessoa
 * Inclui a lista de totais de cada pessoa e o total geral
 */
export interface ConsultaTotaisPorPessoa {
  /** Lista com os totais de cada pessoa */
  totaisPorPessoa: TotalPorPessoa[];
  /** Totais gerais de todas as pessoas */
  totalGeral: TotalGeral;
}

/**
 * Interface para resposta da consulta de totais por categoria
 * Inclui a lista de totais de cada categoria e o total geral
 */
export interface ConsultaTotaisPorCategoria {
  /** Lista com os totais de cada categoria */
  totaisPorCategoria: TotalPorCategoria[];
  /** Totais gerais de todas as categorias */
  totalGeral: TotalGeral;
}
