/**
 * Componente de gerenciamento de Transações
 * Permite listar e criar transações financeiras (receitas e despesas)
 * 
 * Funcionalidades:
 * - Listagem de todas as transações cadastradas
 * - Formulário para criar nova transação
 * - Validação de regras de negócio:
 *   1. Menores de idade só podem ter despesas
 *   2. Categoria deve ser compatível com tipo de transação
 * - Filtros para facilitar a visualização
 * - Exibição de informações da pessoa e categoria
 */

import React, { useState, useEffect } from 'react';
import { 
  Transacao, 
  CriarTransacaoDto, 
  TipoTransacao, 
  Pessoa, 
  Categoria,
  Finalidade 
} from '../types';
import { listarTransacoes, criarTransacao } from '../services/transacaoService';
import { listarPessoas } from '../services/pessoaService';
import { listarCategorias } from '../services/categoriaService';
import '../styles/Transacoes.css';

const Transacoes: React.FC = () => {
  // Estados para dados
  const [transacoes, setTransacoes] = useState<Transacao[]>([]);
  const [pessoas, setPessoas] = useState<Pessoa[]>([]);
  const [categorias, setCategorias] = useState<Categoria[]>([]);
  
  // Estados de controle
  const [loading, setLoading] = useState<boolean>(false);
  const [erro, setErro] = useState<string>('');
  const [sucesso, setSucesso] = useState<string>('');
  
  // Estado do formulário
  const [novaTransacao, setNovaTransacao] = useState<CriarTransacaoDto>({
    descricao: '',
    valor: 0,
    tipo: TipoTransacao.Despesa,
    categoriaId: 0,
    pessoaId: 0
  });

  /**
   * Carrega os dados iniciais ao montar o componente
   */
  useEffect(() => {
    carregarDados();
  }, []);

  /**
   * Carrega todas as transações, pessoas e categorias
   */
  const carregarDados = async () => {
    try {
      setLoading(true);
      setErro('');
      
      // Carrega todos os dados em paralelo para melhor performance
      const [transacoesData, pessoasData, categoriasData] = await Promise.all([
        listarTransacoes(),
        listarPessoas(),
        listarCategorias()
      ]);
      
      setTransacoes(transacoesData);
      setPessoas(pessoasData);
      setCategorias(categoriasData);
    } catch (error) {
      setErro(error instanceof Error ? error.message : 'Erro ao carregar dados');
    } finally {
      setLoading(false);
    }
  };

  /**
   * Função para lidar com mudanças nos campos do formulário
   */
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    
    setNovaTransacao(prev => ({
      ...prev,
      [name]: 
        name === 'valor' ? parseFloat(value) || 0 :
        name === 'categoriaId' || name === 'pessoaId' ? parseInt(value) || 0 :
        value
    }));
  };

  /**
   * Verifica se a pessoa selecionada é menor de idade
   * @returns true se for menor de idade (< 18 anos)
   */
  const pessoaSelecionadaEMenor = (): boolean => {
    const pessoa = pessoas.find(p => p.id === novaTransacao.pessoaId);
    return pessoa ? pessoa.idade < 18 : false;
  };

  /**
   * Filtra as categorias disponíveis baseado no tipo de transação selecionado
   * Regra: Se tipo for Despesa, só mostra categorias com finalidade Despesa ou Ambas
   *        Se tipo for Receita, só mostra categorias com finalidade Receita ou Ambas
   * @returns Array de categorias compatíveis
   */
  const getCategoriasCompativeis = (): Categoria[] => {
    return categorias.filter(categoria => {
      if (novaTransacao.tipo === TipoTransacao.Despesa) {
        return categoria.finalidade === Finalidade.Despesa || 
               categoria.finalidade === Finalidade.Ambas;
      } else {
        return categoria.finalidade === Finalidade.Receita || 
               categoria.finalidade === Finalidade.Ambas;
      }
    });
  };

  /**
   * Valida as regras de negócio antes de criar a transação
   * @returns true se válido, false caso contrário
   */
  const validarTransacao = (): boolean => {
    // Validação: Menor de idade só pode ter despesas
    if (pessoaSelecionadaEMenor() && novaTransacao.tipo === TipoTransacao.Receita) {
      setErro('Menores de idade (menos de 18 anos) só podem ter despesas registradas');
      return false;
    }

    // Validação: Categoria deve ser compatível com o tipo
    const categoria = categorias.find(c => c.id === novaTransacao.categoriaId);
    if (categoria) {
      if (novaTransacao.tipo === TipoTransacao.Despesa && categoria.finalidade === Finalidade.Receita) {
        setErro('Esta categoria só pode ser usada para receitas');
        return false;
      }
      if (novaTransacao.tipo === TipoTransacao.Receita && categoria.finalidade === Finalidade.Despesa) {
        setErro('Esta categoria só pode ser usada para despesas');
        return false;
      }
    }

    return true;
  };

  /**
   * Submete o formulário para criar uma nova transação
   */
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    // Limpa mensagens
    setErro('');
    setSucesso('');

    // Validações básicas
    if (!novaTransacao.descricao.trim()) {
      setErro('Descrição é obrigatória');
      return;
    }

    if (novaTransacao.valor <= 0) {
      setErro('Valor deve ser maior que zero');
      return;
    }

    if (novaTransacao.pessoaId === 0) {
      setErro('Selecione uma pessoa');
      return;
    }

    if (novaTransacao.categoriaId === 0) {
      setErro('Selecione uma categoria');
      return;
    }

    // Validações de regras de negócio
    if (!validarTransacao()) {
      return;
    }

    try {
      setLoading(true);
      await criarTransacao(novaTransacao);
      setSucesso('Transação criada com sucesso!');
      
      // Limpa o formulário
      setNovaTransacao({
        descricao: '',
        valor: 0,
        tipo: TipoTransacao.Despesa,
        categoriaId: 0,
        pessoaId: 0
      });
      
      // Recarrega as transações
      const transacoesData = await listarTransacoes();
      setTransacoes(transacoesData);
    } catch (error) {
      setErro(error instanceof Error ? error.message : 'Erro ao criar transação');
    } finally {
      setLoading(false);
    }
  };

  /**
   * Obtém o nome da pessoa pelo ID
   */
  const getNomePessoa = (pessoaId: number): string => {
    const pessoa = pessoas.find(p => p.id === pessoaId);
    return pessoa ? pessoa.nome : 'Desconhecido';
  };

  /**
   * Obtém a descrição da categoria pelo ID
   */
  const getDescricaoCategoria = (categoriaId: number): string => {
    const categoria = categorias.find(c => c.id === categoriaId);
    return categoria ? categoria.descricao : 'Desconhecida';
  };

  /**
   * Formata valor monetário para exibição
   */
  const formatarMoeda = (valor: number): string => {
    return valor.toLocaleString('pt-BR', { 
      style: 'currency', 
      currency: 'BRL' 
    });
  };

  /**
   * Calcula estatísticas das transações
   */
  const calcularEstatisticas = () => {
    const totalReceitas = transacoes
      .filter(t => t.tipo === TipoTransacao.Receita)
      .reduce((acc, t) => acc + t.valor, 0);
    
    const totalDespesas = transacoes
      .filter(t => t.tipo === TipoTransacao.Despesa)
      .reduce((acc, t) => acc + t.valor, 0);
    
    const saldo = totalReceitas - totalDespesas;
    
    return { totalReceitas, totalDespesas, saldo };
  };

  const stats = calcularEstatisticas();

  return (
    <div className="transacoes-container">
      <h1>Gerenciamento de Transações</h1>

      {/* Mensagens */}
      {erro && <div className="mensagem erro">{erro}</div>}
      {sucesso && <div className="mensagem sucesso">{sucesso}</div>}

      {/* Estatísticas */}
      {transacoes.length > 0 && (
        <div className="stats-section">
          <div className="stat-card receita">
            <span className="stat-label">Total Receitas:</span>
            <span className="stat-value">{formatarMoeda(stats.totalReceitas)}</span>
          </div>
          <div className="stat-card despesa">
            <span className="stat-label">Total Despesas:</span>
            <span className="stat-value">{formatarMoeda(stats.totalDespesas)}</span>
          </div>
          <div className={`stat-card ${stats.saldo >= 0 ? 'saldo-positivo' : 'saldo-negativo'}`}>
            <span className="stat-label">Saldo:</span>
            <span className="stat-value">{formatarMoeda(stats.saldo)}</span>
          </div>
        </div>
      )}

      {/* Formulário */}
      <div className="formulario-section">
        <h2>Registrar Nova Transação</h2>
        
        {/* Avisos importantes */}
        {pessoas.length === 0 && (
          <div className="mensagem aviso">
            ⚠️ Você precisa cadastrar pelo menos uma pessoa antes de criar transações.
          </div>
        )}
        
        {categorias.length === 0 && (
          <div className="mensagem aviso">
            ⚠️ Você precisa cadastrar pelo menos uma categoria antes de criar transações.
          </div>
        )}

        <form onSubmit={handleSubmit} className="formulario">
          <div className="form-group">
            <label htmlFor="pessoaId">Pessoa:</label>
            <select
              id="pessoaId"
              name="pessoaId"
              value={novaTransacao.pessoaId}
              onChange={handleInputChange}
              disabled={loading || pessoas.length === 0}
              required
            >
              <option value={0}>Selecione uma pessoa</option>
              {pessoas.map(pessoa => (
                <option key={pessoa.id} value={pessoa.id}>
                  {pessoa.nome} - {pessoa.idade} anos
                  {pessoa.idade < 18 ? ' (Menor de idade)' : ''}
                </option>
              ))}
            </select>
            {pessoaSelecionadaEMenor() && (
              <small className="form-hint aviso">
                ⚠️ Esta pessoa é menor de idade. Apenas despesas podem ser registradas.
              </small>
            )}
          </div>

          <div className="form-group">
            <label htmlFor="tipo">Tipo:</label>
            <select
              id="tipo"
              name="tipo"
              value={novaTransacao.tipo}
              onChange={handleInputChange}
              disabled={loading}
              required
            >
              <option value={TipoTransacao.Despesa}>Despesa</option>
              <option value={TipoTransacao.Receita}>
                Receita {pessoaSelecionadaEMenor() ? '(Não disponível para menores)' : ''}
              </option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="categoriaId">Categoria:</label>
            <select
              id="categoriaId"
              name="categoriaId"
              value={novaTransacao.categoriaId}
              onChange={handleInputChange}
              disabled={loading || categorias.length === 0}
              required
            >
              <option value={0}>Selecione uma categoria</option>
              {getCategoriasCompativeis().map(categoria => (
                <option key={categoria.id} value={categoria.id}>
                  {categoria.descricao} ({categoria.finalidade})
                </option>
              ))}
            </select>
            <small className="form-hint">
              Apenas categorias compatíveis com o tipo selecionado são exibidas
            </small>
          </div>

          <div className="form-group">
            <label htmlFor="descricao">Descrição:</label>
            <textarea
              id="descricao"
              name="descricao"
              value={novaTransacao.descricao}
              onChange={handleInputChange}
              placeholder="Ex: Compra no supermercado, Pagamento salário..."
              rows={3}
              disabled={loading}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="valor">Valor (R$):</label>
            <input
              type="number"
              id="valor"
              name="valor"
              value={novaTransacao.valor || ''}
              onChange={handleInputChange}
              placeholder="0.00"
              step="0.01"
              min="0.01"
              disabled={loading}
              required
            />
          </div>

          <button 
            type="submit" 
            className="btn btn-primary" 
            disabled={loading || pessoas.length === 0 || categorias.length === 0}
          >
            {loading ? 'Salvando...' : 'Registrar Transação'}
          </button>
        </form>
      </div>

      {/* Lista de transações */}
      <div className="lista-section">
        <h2>Transações Registradas</h2>
        {loading && <p>Carregando...</p>}
        
        {!loading && transacoes.length === 0 && (
          <p className="mensagem-vazia">Nenhuma transação registrada ainda.</p>
        )}

        {!loading && transacoes.length > 0 && (
          <div className="tabela-wrapper">
            <table className="tabela">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Pessoa</th>
                  <th>Tipo</th>
                  <th>Categoria</th>
                  <th>Descrição</th>
                  <th>Valor</th>
                </tr>
              </thead>
              <tbody>
                {transacoes.map((transacao) => (
                  <tr key={transacao.id}>
                    <td>{transacao.id}</td>
                    <td>{getNomePessoa(transacao.pessoaId)}</td>
                    <td>
                      <span className={`badge ${
                        transacao.tipo === TipoTransacao.Receita 
                          ? 'badge-success' 
                          : 'badge-danger'
                      }`}>
                        {transacao.tipo}
                      </span>
                    </td>
                    <td>{getDescricaoCategoria(transacao.categoriaId)}</td>
                    <td>{transacao.descricao}</td>
                    <td className={
                      transacao.tipo === TipoTransacao.Receita 
                        ? 'valor-positivo' 
                        : 'valor-negativo'
                    }>
                      {transacao.tipo === TipoTransacao.Receita ? '+' : '-'}
                      {formatarMoeda(transacao.valor)}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Transacoes;
