/**
 * Componente de Consulta de Totais por Pessoa
 * Exibe um relatÃ³rio com os totais de receitas, despesas e saldo de cada pessoa
 * AlÃ©m de um total geral consolidado
 * 
 * Funcionalidades:
 * - Lista todas as pessoas com seus respectivos totais
 * - Calcula e exibe o saldo de cada pessoa (receitas - despesas)
 * - Exibe o total geral de todas as pessoas
 * - Destaca visualmente saldos positivos e negativos
 */

import React, { useState, useEffect } from 'react';
import { TotalsByPersonQuery } from '../types';
import { queryTotalsByPerson } from '../services/queryService';
import '../styles/Consultas.css';

const TotaisPorPessoa: React.FC = () => {
  // Estado para armazenar os dados da consulta
  const [dados, setDados] = useState<TotalsByPersonQuery | null>(null);
  
  // Estados de controle
  const [loading, setLoading] = useState<boolean>(false);
  const [erro, setErro] = useState<string>('');

  /**
   * Carrega os dados ao montar o componente
   */
  useEffect(() => {
    loadData();
  }, []);

  /**
   * FunÃ§Ã£o para carregar os totais por pessoa do backend
   */
  const loadData = async () => {
    try {
      setLoading(true);
      setErro('');
      const resultado = await queryTotalsByPerson();
      setDados(resultado);
    } catch (error) {
      setErro(error instanceof Error ? error.message : 'Erro ao carregar totais por pessoa');
    } finally {
      setLoading(false);
    }
  };

  /**
   * Formata valor monetÃ¡rio para exibiÃ§Ã£o em Real Brasileiro
   */
  const formatCurrency = (value: number): string => {
    return value.toLocaleString('pt-BR', { 
      style: 'currency', 
      currency: 'BRL' 
    });
  };

  /**
   * Retorna a classe CSS apropriada baseada no saldo
   * @param balance - Valor do saldo
   * @returns Nome da classe CSS
   */
  const getBalanceClass = (balance: number): string => {
    if (balance > 0) return 'saldo-positivo';
    if (balance < 0) return 'saldo-negativo';
    return 'saldo-neutro';
  };

  return (
    <div className="consulta-container">
      <h1>Totais por Pessoa</h1>
      <p className="descricao">
        Visualize o total de receitas, despesas e saldo de cada pessoa cadastrada no sistema.
      </p>

      {/* Mensagem de erro */}
      {erro && <div className="mensagem erro">{erro}</div>}

      {/* Loading */}
      {loading && <div className="loading">Carregando dados...</div>}

      {/* ConteÃºdo */}
      {!loading && dados && (
        <>
          {/* Tabela de totais por pessoa */}
          {dados.totaisPorPessoa.length === 0 ? (
            <div className="mensagem-vazia">
              Nenhuma pessoa cadastrada ou sem transaÃ§Ãµes registradas.
            </div>
          ) : (
            <div className="tabela-wrapper">
              <table className="tabela tabela-consulta">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Nome</th>
                    <th>Idade</th>
                    <th>Total Receitas</th>
                    <th>Total Despesas</th>
                    <th>Saldo</th>
                  </tr>
                </thead>
                <tbody>
                  {dados.totaisPorPessoa.map((item) => (
                    <tr key={item.pessoa.id}>
                      <td>{item.pessoa.id}</td>
                      <td className="nome-pessoa">
                        {item.pessoa.nome}
                        {item.pessoa.idade < 18 && (
                          <span className="badge badge-warning badge-small">Menor</span>
                        )}
                      </td>
                      <td>{item.pessoa.idade} anos</td>
                      <td className="valor-positivo">{formatCurrency(item.totalReceitas)}</td>
                      <td className="valor-negativo">{formatCurrency(item.totalDespesas)}</td>
                      <td className={getBalanceClass(item.saldo)}>
                        <strong>{formatCurrency(item.saldo)}</strong>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {/* Total Geral */}
          <div className="total-geral-section">
            <h2>Total Geral</h2>
            <div className="total-geral-cards">
              <div className="total-card receita">
                <div className="total-card-label">Total de Receitas</div>
                <div className="total-card-valor">
                  {formatCurrency(dados.totalGeral.totalReceitas)}
                </div>
              </div>
              
              <div className="total-card despesa">
                <div className="total-card-label">Total de Despesas</div>
                <div className="total-card-valor">
                  {formatCurrency(dados.totalGeral.totalDespesas)}
                </div>
              </div>
              
              <div className={`total-card ${getBalanceClass(dados.totalGeral.saldoLiquido)}`}>
                <div className="total-card-label">Saldo LÃ­quido</div>
                <div className="total-card-valor">
                  {formatCurrency(dados.totalGeral.saldoLiquido)}
                </div>
                <div className="total-card-info">
                  {dados.totalGeral.saldoLiquido >= 0 
                    ? 'âœ“ Saldo positivo' 
                    : 'âš  Saldo negativo'}
                </div>
              </div>
            </div>
          </div>

          {/* BotÃ£o para atualizar */}
          <div className="acoes-section">
            <button 
              className="btn btn-secondary" 
              onClick={loadData}
              disabled={loading}
            >
              ðŸ”„ Atualizar Dados
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default TotaisPorPessoa;
