/**
 * Componente de Consulta de Totais por Categoria (Funcionalidade Opcional)
 * Exibe um relat√≥rio com os totais de receitas, despesas e saldo de cada categoria
 * Al√©m de um total geral consolidado
 * 
 * Funcionalidades:
 * - Lista todas as categorias com seus respectivos totais
 * - Calcula e exibe o saldo de cada categoria (receitas - despesas)
 * - Exibe o total geral de todas as categorias
 * - Destaca visualmente saldos positivos e negativos
 * - Exibe a finalidade de cada categoria
 */

import React, { useState, useEffect } from 'react';
import { ConsultaTotaisPorCategoria, Finalidade } from '../types';
import { consultarTotaisPorCategoria } from '../services/consultaService';
import '../styles/Consultas.css';

const TotaisPorCategoria: React.FC = () => {
  // Estado para armazenar os dados da consulta
  const [dados, setDados] = useState<ConsultaTotaisPorCategoria | null>(null);
  
  // Estados de controle
  const [loading, setLoading] = useState<boolean>(false);
  const [erro, setErro] = useState<string>('');

  /**
   * Carrega os dados ao montar o componente
   */
  useEffect(() => {
    carregarDados();
  }, []);

  /**
   * Fun√ß√£o para carregar os totais por categoria do backend
   */
  const carregarDados = async () => {
    try {
      setLoading(true);
      setErro('');
      const resultado = await consultarTotaisPorCategoria();
      setDados(resultado);
    } catch (error) {
      setErro(error instanceof Error ? error.message : 'Erro ao carregar totais por categoria');
    } finally {
      setLoading(false);
    }
  };

  /**
   * Formata valor monet√°rio para exibi√ß√£o em Real Brasileiro
   */
  const formatarMoeda = (valor: number): string => {
    return valor.toLocaleString('pt-BR', { 
      style: 'currency', 
      currency: 'BRL' 
    });
  };

  /**
   * Retorna a classe CSS apropriada baseada no saldo
   */
  const getClasseSaldo = (saldo: number): string => {
    if (saldo > 0) return 'saldo-positivo';
    if (saldo < 0) return 'saldo-negativo';
    return 'saldo-neutro';
  };

  /**
   * Retorna a classe CSS do badge baseado na finalidade
   */
  const getBadgeClass = (finalidade: Finalidade): string => {
    switch (finalidade) {
      case Finalidade.Despesa:
        return 'badge badge-danger';
      case Finalidade.Receita:
        return 'badge badge-success';
      case Finalidade.Ambas:
        return 'badge badge-info';
      default:
        return 'badge';
    }
  };

  return (
    <div className="consulta-container">
      <h1>Totais por Categoria</h1>
      <p className="descricao">
        Visualize o total de receitas, despesas e saldo de cada categoria cadastrada no sistema.
      </p>

      {/* Mensagem de erro */}
      {erro && <div className="mensagem erro">{erro}</div>}

      {/* Loading */}
      {loading && <div className="loading">Carregando dados...</div>}

      {/* Conte√∫do */}
      {!loading && dados && (
        <>
          {/* Tabela de totais por categoria */}
          {dados.totaisPorCategoria.length === 0 ? (
            <div className="mensagem-vazia">
              Nenhuma categoria cadastrada ou sem transa√ß√µes registradas.
            </div>
          ) : (
            <div className="tabela-wrapper">
              <table className="tabela tabela-consulta">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Descri√ß√£o</th>
                    <th>Finalidade</th>
                    <th>Total Receitas</th>
                    <th>Total Despesas</th>
                    <th>Saldo</th>
                  </tr>
                </thead>
                <tbody>
                  {dados.totaisPorCategoria.map((item) => (
                    <tr key={item.categoria.id}>
                      <td>{item.categoria.id}</td>
                      <td className="descricao-categoria">
                        {item.categoria.descricao}
                      </td>
                      <td>
                        <span className={getBadgeClass(item.categoria.finalidade)}>
                          {item.categoria.finalidade}
                        </span>
                      </td>
                      <td className="valor-positivo">{formatarMoeda(item.totalReceitas)}</td>
                      <td className="valor-negativo">{formatarMoeda(item.totalDespesas)}</td>
                      <td className={getClasseSaldo(item.saldo)}>
                        <strong>{formatarMoeda(item.saldo)}</strong>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {/* Total Geral */}
          <div className="total-geral-section">
            <h2>Total Geral</h2>
            <div className="total-geral-cards">
              <div className="total-card receita">
                <div className="total-card-label">Total de Receitas</div>
                <div className="total-card-valor">
                  {formatarMoeda(dados.totalGeral.totalReceitas)}
                </div>
              </div>
              
              <div className="total-card despesa">
                <div className="total-card-label">Total de Despesas</div>
                <div className="total-card-valor">
                  {formatarMoeda(dados.totalGeral.totalDespesas)}
                </div>
              </div>
              
              <div className={`total-card ${getClasseSaldo(dados.totalGeral.saldoLiquido)}`}>
                <div className="total-card-label">Saldo L√≠quido</div>
                <div className="total-card-valor">
                  {formatarMoeda(dados.totalGeral.saldoLiquido)}
                </div>
                <div className="total-card-info">
                  {dados.totalGeral.saldoLiquido >= 0 
                    ? '‚úì Saldo positivo' 
                    : '‚ö† Saldo negativo'}
                </div>
              </div>
            </div>

            {/* Informa√ß√£o adicional sobre o relat√≥rio */}
            <div className="info-box">
              <h3>üí° Como interpretar este relat√≥rio</h3>
              <ul>
                <li>
                  <strong>Categorias de Despesa:</strong> Mostram apenas valores negativos (despesas)
                </li>
                <li>
                  <strong>Categorias de Receita:</strong> Mostram apenas valores positivos (receitas)
                </li>
                <li>
                  <strong>Categorias Ambas:</strong> Podem ter tanto receitas quanto despesas
                </li>
                <li>
                  <strong>Saldo:</strong> Resultado da diferen√ßa entre receitas e despesas da categoria
                </li>
              </ul>
            </div>
          </div>

          {/* Bot√£o para atualizar */}
          <div className="acoes-section">
            <button 
              className="btn btn-secondary" 
              onClick={carregarDados}
              disabled={loading}
            >
              üîÑ Atualizar Dados
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default TotaisPorCategoria;
